import React from 'react';
import axios from 'axios';

class App extends React.Component {

	state = {
		details : [],
	}

	componentDidMount() {

		let data ;

		axios.get('http://127.0.0.1:8000/choosequiztype/')
		.then(res => {
			data = res.data;
			this.setState({
				details : data	
			});
			console.log('data:',data)
		})
		.catch(err => {})
	}

    render() {
		const details=this.state;
	    return (
			
		  	
			<div className="home">
				{details.map((each_quiz, index) => 
					<div className='card' key={index} {...each_quiz}>
						<div id={each_quiz.id}>  
			 			<td>{each_quiz.names_of_quiz}</td>  
						<td>{each_quiz.description}</td>  
						<td>{each_quiz.number_of_questions}</td>  
						</div>  
					</div>
				)}  
			</div>
			
		);
                    
    }
}

export default App;
