import React from 'react';
import axios from 'axios';

class App extends React.Component {

	state = {
		details : [],
	}

	componentDidMount() {

		let data ;

		axios.get('http://127.0.0.1:8000/choosequiztype/')
		.then(res => {
			data = res.data;
			this.setState({
				details : data	
			});
			console.log('data:',data)
		})
		.catch(err => {})
	}

	render() {
		return (
			<div>
		  		{details.map(el => {
					return (
						<div  key={el.id}>
							<div>{el.names_of_quiz}</div>
							<div>{el.description}</div>
						</div>
					);
				})}
			</div>

				  
		);
	}
}

export default App;
